#Rosalind problem #1 : Counting DNA Nucleotides 
dna = ""
nucleotides = list(dna)
A = 0
T = 0
C = 0
G = 0
for i in range(0,len(nucleotides)):
  if (nucleotides[i] == "A"):
    A += 1
  if (nucleotides[i] == "T"):
    T += 1
  if (nucleotides[i] == "C"):
    C += 1
  if (nucleotides[i] == "G"):
    G += 1

print("%d %d %d %d"%(A,C,G,T))

#Rosalind Problem #2 : Transcribing DNA into RNA 
dna = ""
nucleotides = list(dna)
for i in range(0,len(nucleotides)):
  if (nucleotides[i] == "T"):
    nucleotides[i] = "U"
rna = "".join(nucleotides)
print(rna)